import React, {Fragment, useEffect, useState} from "react";
import EditTodos from "./editTodo";
import './listTodo.css'

const ListTodos = () => {

    const [todos, setTodos] = useState([]);

    //delete function 
    const deleteTodo = async id => {
        try {
            const deleteTodo = await fetch(`http://localhost:5000/todos/${id}`, {
                method: "DELETE"
            });
            setTodos(todos.filter(todo => todo.todo_id !== id));
        } catch (err) {
            console.error(err.message)
        }
    };

    const getTodos = async () => {
        try {
            const response = await fetch("http://localhost:5000/todos");
            const jsonData = await response.json();

            setTodos(jsonData);
        } catch (err) {
            console.error(err.message)
        }
    };

    useEffect(() => {
        getTodos();
    }, []);

    return (
    <Fragment>
        <table class="table mt-5 text-center">
            <thead>
            <tr>
                <th>Check</th>
                <th scope="col">Things to do:</th>
                <th scope="col"></th>
                <th scope="col"></th>
            </tr>
            </thead>
            <tbody>
                {todos.map(todo => (
                    <tr key={todo.todo_id}>
                        <td><input type="checkbox" class="checkbox"/></td>
                        <th scope="row">{todo.description}</th>
                        <td>
                            <EditTodos todo={todo} />
                        </td>
                        <td>
                            <button type="button" class="btn danger" data-bs-toggle="alert" onClick={() => deleteTodo(todo.todo_id)}>Delete</button>
                            <div class="modal fade" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                <div class="modal-dialog">
                                    <div class="modal-content">
                                        <div class="modal-header">
                                            <h1 class="modal-title fs-5" id="exampleModalLabel">Your task :</h1>
                                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" ></button>
                                        </div>
                                        <div class="modal-body">
                                            <input type="text" className="form-control edit" value={description} onChange={e => setDescription(e.target.value)}/>
                                        </div>
                                    <div class="modal-footer">
                            <button type="button" class="btn cancel" data-bs-dismiss="modal" onClick={() => setDescription(todo.description)}>Cancel Changes</button>
                            <button type="button" class="btn success" onClick={e => updateData(e)}>Save it</button>
                        </div>
                    </div>
                </div>
            </div>
                        </td>
                    </tr>
                ))}

            </tbody>
        </table>
    </Fragment>
    );
};

export default ListTodos;
